        if prio < 9 and (aura_env.marks[prio] == nil or GetTime()-aura_env.marks[prio].time > 60) then\n            markID = prio\n            msg = (\"ADD_MARK:\"..(markID)..\":\"..guid..\":\"..GetTime())\n            break\n        end\n    end\n    if aura_env.config.settings.noPriorityToggle == 2 and not markID then \n        -- Random marks are activated.\n        for i = 1, 8, 1 do \n            if (aura_env.marks[i] == nil or GetTime()-aura_env.marks[i].time > 60) and aura_env.config.settings.noPriorityMarks[9-i] and \n            aura_env.playerInfo[\"markTankID\"] ~= i and aura_env.playerInfo[\"markHealerID\"] ~= i then \n                markID = i\n                msg = (\"ADD_MARK:\"..markID..\":\"..guid..\":\"..GetTime())\n                break\n            end\n        end\n    end\n    if markID then \n        aura_env.marks[markID] = {\n            [\"guid\"] = guid,\n            [\"time\"] = GetTime(),\n        }\n        --print(\"|TInterface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_\"..markID..\":16|t : \"..guid..\" : \"..GetTime())\n        C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", msg, aura_env.GetChatType())\n        if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.marks, \"aura_env.marks\") end\n        return SetRaidTarget(unitId, markID)\n    end\nend\n\naura_env.CreateMobsTable = function(instanceID)\n    if type(instanceID) ~= \"string\" then \n        instanceID = tostring(instanceID)\n    end\n    if aura_env.config[instanceID] then -- Player is in supported Dungeon\n        for i = 1, 3, 1 do\n            local pointer = (i == 1) and aura_env.config[instanceID] or (i == 2) and aura_env.config[instanceID..\":2\"] or aura_env.config[\"seasonal\"] \n            if pointer then \n                for _,v in ipairs(pointer) do \n                    v.npcID = v.npcID:gsub(\"%s+\", \"\")\n                    if v.npcID:find(\";\") then\n                        -- Multiple npc IDs found\n                        local tbl = { strsplit(\";\", v.npcID) }\n                        for _,k in pairs(tbl) do \n                            --if k == nil then break end -- error checking\n                            if v.autoMarkOption ~= 1 then \n                                aura_env.mobs[tonumber(k)] = {\n                                    [\"name\"] = v.desc,\n                                    [\"autoMarkOption\"] = v.autoMarkOption,\n                                    [\"P1\"] = v.P1,\n                                    [\"P2\"] = v.P2,\n                                    [\"P3\"] = v.P3,\n                                    [\"P4\"] = v.P4,\n                                }\n                            end\n                        end\n                    else\n                        if v.autoMarkOption ~= 1 then \n                            aura_env.mobs[tonumber(v.npcID)] = {\n                                [\"name\"] = v.desc,\n                                [\"autoMarkOption\"] = v.autoMarkOption,\n                                [\"P1\"] = v.P1,\n                                [\"P2\"] = v.P2,\n                                [\"P3\"] = v.P3,\n                                [\"P4\"] = v.P4,\n                            }\n                        end\n                    end\n                end\n            end\n        end\n    end\n    if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.mobs, \"aura_env.mobs\") end\nend\n\naura_env.updatePlayerMarks = function()\n    if aura_env.playerInfo[\"markPlayers\"] then \n        for i = 0, GetNumGroupMembers(), 1 do \n            local pointer = i == 0 and \"player\" or (\"party\"..i)\n            if UnitGroupRolesAssigned(pointer) == \"TANK\" then \n                if GetRaidTargetIndex(pointer) ~= (aura_env.playerInfo[\"markTankID\"]) and aura_env.playerInfo[\"markTankID\"] < 9 then \n                    SetRaidTarget(pointer, (aura_env.playerInfo[\"markTankID\"]))\n                end\n            end\n