RLD\" then\n        aura_env.check = false\n        aura_env.healers = {}\n        \n        for k, v in pairs(states) do\n            v.show = false\n            v.changed = true\n        end\n        \n        C_Timer.After(2, function() WeakAuras.ScanEvents(\"FIND_HEALERS\", true) end) --to give lib time to gather info\n        return true\n    end\n    \n    if event == \"HEALER_INFO\" and ... then\n        for unit, data in pairs(aura_env.healers) do\n            if data[7] and (data[7] ~= WeakAuras.myGUID or (data[7] == WeakAuras.myGUID and c.showSelf)) then\n                states[unit] = {\n                    unitid = unit,\n                    status = \"\",\n                    sound = false,\n                    name = RAID_CLASS_COLORS[data[3]]:WrapTextInColorCode(data[2]),\n                    icon = data[6],\n                    classIcon = data[6],\n                    iconGlow = false,\n                    duration = 0,\n                    expirationTime = 0,\n                    progressType = \"timed\",\n                    value = data[4],\n                    total = data[5],\n                    percent = data[5] == 0 and 0 or data[4]/data[5]*100,\n                    autoHide = false,\n                    show = true,\n                    changed = true,    \n                }\n                aura_env.flag = true\n            end\n        end\n        return true\n    end\n    \n    if event == \"UNIT_POWER_UPDATE\" then\n        local unit = ...\n        local state = states[unit]\n        if state and state.status == \"\" then\n            state.value = UnitPower(unit, Enum.PowerType.Mana)\n            state.total = UnitPowerMax(unit, Enum.PowerType.Mana)\n            state.percent = state.total == 0 and 0 or state.value/state.total*100\n            state.changed = true\n            \n            aura_env.flag = true\n            return true\n        end\n    end\n    \n    if event == \"FRAME_UPDATE\" then\n        local theTime = GetTime()\n        if not aura_env.last or aura_env.last < theTime - c.throttle then\n            aura_env.last = theTime\n            for k, v in pairs(states) do\n                if v.show and v.unitid then\n                    local unit = v.unitid\n                    local state = states[unit]\n                    local mana, ss, visible, dead\n                    local connected = UnitIsConnected(unit)\n                    if connected then\n                        visible = UnitIsVisible(unit)\n                        dead = UnitIsDeadOrGhost(unit)\n                    end\n                    if visible and not dead then\n                        mana, ss = aura_env.GetUnitAura(unit)\n                    end\n                    if state.sound and not drinking then\n                        state.sound = false\n                        state.changed = true\n                    end\n                    if mana then\n                        if not state.sound then\n                            state.sound = c.sound\n                        end\n                        state.icon = mana[10] == 252753 and 2057566 or mana[10] == 29166 and 136048 or 132805\n                        state.duration = mana[5]\n                        state.expirationTime = mana[6]\n                        state.status = \"\"\n                        state.iconGlow = true\n                        state.changed = true\n                    elseif ss then\n                        state.icon = 136210\n                        state.duration = ss[5]\n                        state.expirationTime = ss[6]\n                        state.status = \"\"\n                        state.iconGlow = false\n                        state.changed = true\n                    elseif not connected then\n                        state.icon = 132281\n                        state.status = \"OFFLINE\"\n                        state.percent = 0\n                        state.iconGlow = false\n                        state.changed = true\n                        if c.offline then\n                            aura_env.flag = true\n                        end\n                    elseif not visible then\n                        state.icon = 237381\n                        state.status = \"FAR\"\n                        state.percent = 0\n                        state.iconGlow = false\n                        state.changed = true\n                        if c.visible then\n                            aura_env.flag = true\n                        end\n                    elseif dead then\n                        state.icon = 237272\n                        state.status = \"DEAD\"\n                        state.percent = 0\n                        state.iconGlow = false\n                        state.changed = true\n                        if c.dead then\n                            aura_env.flag = true\n                        end\n                    elseif state.icon ~= state.classIcon then\n                        state.duration = 0\n                        state.expirationTime = 0\n                        state.icon = states[unit].classIcon\n                        state.value = UnitPower(unit, Enum.PowerType.Mana)\n                        state.total = UnitPowerMax(unit, Enum.PowerType.Mana)\n                        state.percent = states[unit].total == 0 and 0 or states[unit].value/states[unit].total*100\n                        state.status = \"\"\n                        state.iconGlow = false\n                        state.changed = true\n                        if c.dead or c.visible or c.offline then\n                            aura_env.flag = true\n                        end\n                    end\n                end\n            end\n            return true\n        end\n    end\nend",
