						["custom_type"] = "event",
						["subeventSuffix"] = "_CAST_START",
						["custom"] = "--UNIT_AURA UNIT_SPELLCAST_CHANNEL_START UNIT_SPELLCAST_CHANNEL_STOP COMBAT_LOG_EVENT_UNFILTERED\nfunction(event, ...)\n    if not aura_env.config[\"display\"][\"showGlow\"] then return false end\n    \n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local event2 = select(2, ...)\n        if event2 == \"SPELL_CAST_SUCCESS\" then  --施法成功计时器\n            local GUID = select(4, ...)\n            local spellId = select(12, ...)\n            \n            if aura_env.GUIDStates[GUID] and aura_env.GUIDStates[GUID][spellId] then\n                local state = aura_env.GUIDStates[GUID][spellId]\n                \n                if state.glowCondiction and state.glowCondiction.type and\n                state.glowCondiction.type == \"TIMER\" then\n                    if not state.glow then\n                        state.glow = true\n                        state.changed = true\n                        WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                    end\n                    if aura_env.castTimer[GUID..spellId] then\n                        aura_env.castTimer[GUID..spellId]:Cancel()\n                    end\n                    aura_env.castTimer[GUID..spellId] = C_Timer.NewTimer(state.glowCondiction.time, function()\n                            if state.glow then\n                                state.glow = false\n                                state.changed = true\n                                WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                            end\n                    end)\n                end\n            end\n        end\n    elseif event == \"UNIT_AURA\" then    --光环检测\n        local unit = ...\n        if unit then\n            local GUID = UnitGUID(unit)\n            \n            if aura_env.GUIDStates[GUID] then\n                local states = aura_env.GUIDStates[GUID]\n                for spellId, state in pairs(states) do\n                    if state.glowCondiction and state.glowCondiction.type and\n                    state.glowCondiction.type == \"AURA\" then\n                        local id = state.glowCondiction.spellId\n                        if not id then id = spellId end\n                        if WA_GetUnitBuff(unit, id) then\n                            if not state.glow then\n                                state.glow = true\n                                state.changed = true\n                                WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                            end\n                        else\n                            if state.glow then\n                                state.glow = false\n                                state.changed = true\n                                WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_CHANNEL_START\" or event == \"UNIT_SPELLCAST_CHANNEL_STOP\" then   --引导技能监视\n        local unit, _, spellId = ...\n        if unit and spellId then\n            local GUID = UnitGUID(unit)\n            \n            if aura_env.GUIDStates[GUID] and aura_env.GUIDStates[GUID][spellId] then\n                local state = aura_env.GUIDStates[GUID][spellId]\n                if state.glowCondiction and state.glowCondiction.type and\n                state.glowCondiction.type == \"CHANNEL\" then\n                    if event == \"UNIT_SPELLCAST_CHANNEL_START\" then\n                        if not state.glow then\n                            state.glow = true\n                            state.changed = true\n                            WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                        end\n                    else\n                        if state.glow then\n                            state.glow = false\n                            state.changed = true\n                            WeakAuras.ScanEvents(\"ZTG_GLOW_UPDATE\")\n                        end\n                    end\n                end\n            end\n        end\n    end\nend",
